import React from "react";

import { Swiper, SwiperSlide } from 'swiper/react';
import {Autoplay} from 'swiper'
import img1 from '..../images/online-shopping.jpg';
import img2 from './src/images/shop1.jpg';

//use carousel of images
function LeftWrapper() {
  const imagesArray = [
    {
      id: '1',
      href: img1,
    },
    {
      id: '2',
      href: img2,
    }
  ]
  return (
    <div style={{ backgroundColor: "pink", width: "50vw", height: "100vh" }}>
      <Swiper
         spaceBetween={30}
         centeredSlides={true}
         autoplay={{
           delay: 2500,
           disableOnInteraction: false,
         }}
        
         modules={[Autoplay]}
      >
        {imagesArray.map((img) => <SwiperSlide><img src={img.href}/></SwiperSlide>)}
        
      </Swiper>
    </div>
  );
}

export default LeftWrapper;
